            IF i_end_dt = '0000-00-00 00:00:00' OR i_end_dt = '' OR i_end_dt IS NULL OR next_draft_dt <= i_end_dt THEN
                SELECT 1 INTO ret_val;
            ELSE
                SELECT 2 INTO ret_val;
            END IF;
        ELSE
            SELECT 2 INTO ret_val;
        END IF;
    END IF;
    return ret_val;
END ;;
