CREATE DEFINER=`fairgate`@`%` PROCEDURE `importContacts`(IN `tableName1` TEXT, IN `tableName2` TEXT, IN `p_club_id` INT, IN `changed_by` INT, IN `main_contact` TEXT, IN `own_table` TEXT)
