                    SET gen_cat_id = (SELECT fddc.`id` FROM `fg_dm_document_category` fddc WHERE fddc.`document_type` = 'CONTACT' AND fddc.`club_id` = doc_club_id AND fddc.`title`='General' LIMIT 1);
