                    SELECT `id`,`is_company`, 
                        (SELECT `value` FROM `fg_cm_attribute_value_text` WHERE fg_cm_attribute_value_text.`contact_id` = fg_cm_contact.`id` AND fg_cm_attribute_value_text.`attribute_id` = system_att_company_name),
