BEGIN	
    DECLARE count_account INTEGER DEFAULT 0;
    DECLARE nxt_invoice_no TEXT DEFAULT "";
    DECLARE EXIT HANDLER FOR SQLEXCEPTION, SQLWARNING
    BEGIN
        ROLLBACK;
    END;
	START TRANSACTION;



                DELETE FROM `fg_im_invoice` WHERE `accouting_id`= accountingid AND `club_id`=clubid;


